css:
body{
  font-family: 'Montserrat', sans-serif;
}

/*Fullbanner*/
@media  screen and (min-width: 992px) {
  .banner-slider {
    max-width: 100%!important;
  }
}

/*COR MENU CABECALHO*/
main .header__nav a {
    color: #007062;
}

/*COR ICONES CABECALHO*/
.header__buttons .btn {
    color: #d19e3c;
}

/*COR BARRA DE PESQUISA BORDA*/
main #search-input {
    border: 1px solid #efd9b1 !important;
}

/*COR BOTAO MENU CABECALHO*/
.header__toggler-icon {
    color: #007062;
}

@media screen and (max-width: 480px)
.header {
    top: 27px;
}

/*topo mobile*/
@media screen and (max-width: 480px) {
  .top-bar__countdown {
    font-size: 0.9rem;
    font-weight:500
  }
  .custom-html + div.responsive-baner {
    margin-bottom: 2rem !important;
  }
  #cart-quickview .minicart__aside {
    margin-top: 1rem;
  }
}

.col-xl-5 {
    flex: 0 0 41.66667%;
    max-width: 31.66667%;
    font-size: small;
}

/*RODAPE CELULAR*/
 @media only screen and (max-width: 760px) {
    .col-xl-5 {
    max-width: 70.66667%;
}
}

:root {
--primary-lightest: #008c7b;
}

main .header {
    border-bottom: none;
    padding: 10px;
}

@media (min-width: 768px)
.header__container {
    margin-top: var(--spacer-3);
}

@media (min-width: 768px)
.header__row {
    min-height: 60px;
    padding-top: 10px;
}

/* Tablet and desktop */
@media (min-width: 767px) { 
  .header {
  top: 29px;
  }

  /*BOTAO*/
  .btn-primary {
    background-color: #146153;
    border:none;
    border:solid 1px;
    border-radius:10px;
  }
    /*HOVER BOTAO*/
  .btn-primary:hover {
    background-color:#138273;
    border-color:#ffffff;
  }
}
 /*BOTAO-CARRINHO*/
.btn:not(:disabled):not(.disabled) {
    cursor: pointer;
    border-radius: 10px;
}


@media (min-width: 767px) {
.header {
    top: 29px!important;
}
}


main .header {
    border-bottom: none;
}

.header {
    Background: #ffffff;
    padding-top: 10px;
    padding: var(--spacer-2);
}

@media (min-width: 767px)
.header {
    top: 29px!important;
}

@media (min-width: 576px)
.header {
    border-width: 2px;
    padding: var(--spacer-2) var(--spacer-3);
}

.header {
    position: -webkit-sticky;
    position: sticky;
    top: 0;
    width: 100%;
    background: var(--light);
    padding: var(--spacer-1);
    border-bottom: 1px solid var(--primary-lighter);
    box-shadow: var(--box-shadow-sm);
    z-index: 900;
}

/*BOTÃO COMPRAR FIXO*/
.product-card__buy.fade {
    opacity: 1!important;
    padding-top: 10px;
    color: #007062;
}

/*TAMANHO SELOS RODAPÉ*/
.stamps img {
    max-height: 30px;
}
/*TAMANHO TEXTO CABEÇALHO*/
.header__nav {
    font-size: medium;
    border-radius: 6px;
}

/*HOVER DO CABEÇALHO*/
.header__nav a:hover {
    background-color: #007062;
    color: #ffffff;
    border-radius: 6px;
}

/*COR DO MENU LATERAL*/
.menu__item {
    display: flex;
    align-items: center;
    color: #007062;
    padding: var(--spacer-3);
    transition: background-color .15s;
    border-bottom: var(--border-width) solid #007062;
}

.lead {
    display: none;
}

/*TIRAR NOME DESCRICAO*/

.lead {
    display: none;
}
/*COR DA FORMA DE PAGAMENTO*/
.list-group-item.active {
    z-index: 2;
    color: #ffffff;
    background-color: #d19e3c;
    border-color: #ffffff;
}

/*CORES PAGINA DE CHECKOUT*/
.order-info__financial-status--pending {
    color: #d19e3c;
}

.alert-warning, .alert-warning>td, .alert-warning>th {
    background-color: #d19e3c;
}

.order-info__number {
    color: #007062;
}

.order-info__timeline-status--pending, .order-info__timeline-status--under_analysis {
    border-color: #d19e3c;
}

/*NEGRITO MENSAGEM SUCESSO*/
.order-info__new {
    font-weight: 600;
}
.orders-list__financial-status--pending {
    color: #d19e3c;
}

.orders-list__number--open {
    border-left-color: #007062;
}

@media (min-width: 768px)
.header__container {
    margin-top: var(--spacer-3);
}
/*LISTA RODAPÉ*/
@media (min-width: 576px)
.footer__links {
    font-size: 92%;
}

@media (min-width: 992px)
.order-lg-first {
    order: -1;
    font-size: small;
}

@media (min-width: 1200px)
.col-xl-5 {
    max-width: 31.66667%;
}

/*Teste de barra fixa*/
/*Barra Fixa de Promoção Topo*/

/* Tablet and desktop */
  .header {
  top: 50px;
  }
  .top-bar {
    position: sticky;
    top: 0;
    z-index: 900;
  }
}
/* Mobile */
@media screen and (max-width: 480px) {
  .header {
    top: 27px;
  }
  .top-bar {
    position: sticky;
    top: 0;
    z-index: 900;
  }
}

main .top-bar {
    display: block;
}

main .top-bar__nav {
    font-size: 0px!important;
    padding: 0px!important;
}

@media (min-width: 992px) {
.top-bar__countdown {
  font-size: 0.9rem;
  font-weight:500;
  padding-top: 5px;
  padding-bottom: 3px;
}
}
