css:
body{
  font-family: 'Montserrat', sans-serif;
}

@media  screen and (min-width: 992px) and (max-width: 1367px) {
  .banner-slider {
    max-width: 100% !important;
  }
}
// apenas tablet
@media screen and (min-width: 481px) and (max-width: 991px) {
  .banner-slider {
    max-width: 100% !important;
  }
}

/* Tablet and desktop */
@media (min-width: 767px) { 
  .header {
  top: 50px;
  }
  .top-bar {
    position: sticky;
    top: 0;
    z-index: 900;
  }
  /*BOTAO*/
  .btn-primary {
    background-color: #146153;
    border:none;
    border:solid 1px;
    border-radius:10px;
  }
    /*HOVER BOTAO*/
  .btn-primary:hover {
    background-color: #138273;
    border-color:none;
  }
}
/* Mobile */
@media screen and (max-width: 480px) {
  .header {
    top: 29px;
  }
  .top-bar {
    position: sticky;
    top: 0;
    z-index: 900;
  }
}

main .top-bar {
    display: block;
}

main .top-bar__nav {
    font-size: 0px!important;
    padding: 0px!important;
}

@media (min-width: 992px) {
.top-bar__countdown {
    font-size: 15px!important;
    padding-top: var(--spacer-1);
    padding-bottom: var(--spacer-1);
    margin-bottom: 2px;
}
}

@media (min-width: 767px) {
.header {
    top: 32px!important;
}
}


main .header {
    border-bottom: none;
}

.header {
    Background: #ffffff;
    padding-top: 10px;
    padding: var(--spacer-2);
}

@media (min-width: 767px)
.header {
    top: 38px!important;
}

@media (min-width: 576px)
.header {
    border-width: 2px;
    padding: var(--spacer-2) var(--spacer-3);
}
.header {
    position: -webkit-sticky;
    position: sticky;
    top: 0;
    width: 100%;
    background: var(--light);
    padding: var(--spacer-1);
    border-bottom: 1px solid var(--primary-lighter);
    box-shadow: var(--box-shadow-sm);
    z-index: 900;
}

/*BOTÃO COMPRAR FIXO*/
.product-card__buy.fade {
    opacity: 1!important;
    padding-top: 10px;
    color: #007062;
}

/*TAMANHO SELOS RODAPÉ*/
.stamps img {
    max-height: 30px;
}
/*TAMANHO TEXTO CABEÇALHO*/
.header__nav {
    font-size: medium;
}

/*HOVER DO CABEÇALHO*/
.header__nav a:hover {
    background-color: #007062;
    color: #ffffff;
}

/*COR DO MENU LATERAL*/
.menu__item {
    display: flex;
    align-items: center;
    color: #007062;
    padding: var(--spacer-3);
    border-bottom: #007062;
    transition: background-color .15s;
}
